        public async Task SendAsync(Order order, string subject)
        {
            MailAddress from = new MailAddress("animeshopdwc@mail.ru", "AnimeShop");
            // кому отправляем
            MailAddress to = new MailAddress(order.User.Email);
            // создаем объект сообщения
            MailMessage m = new MailMessage(from, to);
            // тема письма
            m.Subject = subject;
            // текст письма
            m.Body = GetOrderInformationHtml(order.User,order.OrderProducts);
            // письмо представляет код html
            m.IsBodyHtml = true;
            // адрес smtp-сервера и порт, с которого будем отправлять письмо
            SmtpClient smtp = new SmtpClient("smtp.mail.ru", 587);
            // логин и пароль
            smtp.Credentials = new NetworkCredential("animeshopdwc@mail.ru", "HAeDbiMRUuk7h5HgMcPs");
            smtp.EnableSsl = true;
            await smtp.SendMailAsync(m);
        }
            
        public async Task SendAsync(User user, string subject)
